SELECT CAST(EDUCATION_BACKGROUND AS VARCHAR) AS EDUCATION_BACKGROUND, COUNT(*) AS EDUCATION_NUM FROM RECRUITMENT_ANALYSIS.ETL_DATA WHERE CAST(EDUCATION_BACKGROUND AS VARCHAR) != '' AND CAST(CITY AS VARCHAR) IN
#{city}
GROUP BY CAST(EDUCATION_BACKGROUND AS VARCHAR)

SELECT CAST(POSITION AS VARCHAR) AS POSITION, COUNT(*) AS POSITION_NUM FROM RECRUITMENT_ANALYSIS.ETL_DATA WHERE CAST(CITY AS VARCHAR) IN
#{city}
GROUP BY CAST(POSITION AS VARCHAR) ORDER BY POSITION_NUM DESC LIMIT 10
