mapper.select:
SELECT POSITION,SUM(NUM) AS POSITION_COUNT
FROM
(SELECT CAST(POSITION AS VARCHAR) AS POSITION,COUNT(1) AS NUM
FROM "recruitment_analysis"."POSITION_CITY_KEY"
GROUP BY CAST(POSITION AS VARCHAR),CAST(CITY AS VARCHAR)
HAVING CAST(CITY AS VARCHAR) IN

    #{city}

) T
GROUP BY T.POSITION
ORDER BY POSITION_COUNT DESC;
